import { FavoriteBorderOutlined, SearchOutlined, ShoppingCartOutlined } from '@material-ui/icons'
import React from 'react'
import styled from 'styled-components'

const Container = styled.div`
    flex: 1;
    margin: 5px;
    min-width: 250px;
    height: 250px;
    display: flex;
    align-items: center;
    position: relative;
    `

const Circle = styled.div`
    width: 200px;
    height: 200px;
    border-radius: 50%;
    background-color: grey;
    position: absolute;

`
const Image = styled.img`
    width:100%;
    height:100%;
    object-fit:cover;
    z-index: 2;

`
const Info = styled.div`
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    background-color: grey;
    z-index: 3;
    display: flex;
    align-items: center;
    justify-content: center;
`
const Icon = styled.div`
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-color: white;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 5px;
    transition: all 0.6 ease-in;
    &&:hover{
        background-color: #3dc3b6;
        transform: scale(1.2);

    }

`
const Product = ({item}) => {
    return (
        <Container>
            <Circle />
            <Image src={item.img}/>
            <Info>
                <Icon>
                    <ShoppingCartOutlined/>
                </Icon>
                <Icon>
                    <SearchOutlined/>
                </Icon>
                <Icon>
                    <FavoriteBorderOutlined/>
                </Icon>
            </Info>
        </Container>
    )
}

export default Product
