import { ArrowBackIos, ArrowForwardIos} from '@material-ui/icons'
import React from 'react'
import styled from 'styled-components'

const Container = styled.div`

    width: 100%;
    height: 100vh;
    display: flex;
`;

const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: #d475b3;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${props => props.direction === "left" && "10px"};
    right: ${props => props.direction === "right" && "10px"};
    cursor: pointer;
    opacity: 0.5;

    margin: auto;
`;

const Wrapper = styled.div`
    height: 100%;
`;

const Slide = styled.div`
    background-image: url("https://s3-eu-central-1.amazonaws.com/www-staging.esports.com/WP%20Media%20Folder%20-%20esports-com/app/uploads/2021/09/BF2042-Cover.jpg");
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
`;

const ImgContainer = styled.div`
    height: 100%;
    width: 100%;
    flex: 1;
`;
const Image = styled.img`
    height: 80%;

`;
const InfoContainer = styled.div`
    padding: 50px;
    flex: 1;
`;

const Title = styled.h1`
    font-size: 14px;
`;
const Desc = styled.p`

`;
const Button = styled.button`

`; 
const Slider = () => {
    return (
        <Container>
            <Arrow direction="left">
                <ArrowBackIos style={{color: "white"}}/>
            </Arrow>
            <Wrapper>
                <Slide>
                    <ImgContainer>
                        <Image ></Image>
                    </ImgContainer>
                    <InfoContainer>
                        <Title>Battlefield 2042</Title>
                        <Desc>Last chance to pre-purchase Battelfield 2042</Desc>
                        <Button>Buy now</Button>
                    </InfoContainer>
                </Slide>
            </Wrapper>
            <Arrow direction="right">
                <ArrowForwardIos style={{color: "white"}}/>
            </Arrow>
        </Container>
    )
}

export default Slider
