import "./adminSingleProduct.css";
import Button from "mui-button";
import SendIcon from "@material-ui/icons/Send"
import { useLocation } from 'react-router-dom';
import { useDispatch,useSelector } from 'react-redux';
import { useState, useEffect } from "react";
import { updateProducts } from "../../../redux/apiRequests";
import Alert from '@mui/material/Alert';

function AdminSingleProduct() {
    
    
    const productId = useLocation().pathname.split("/")[2];
    const product = useSelector((state) => state.product.products.find(element =>  element._id === productId));
    const dispatch = useDispatch();
    const [ warnings, setWarnings ] = useState({});
    const handleUpdate = (productId) => {
        updateProducts(productId,product,dispatch);
    }






    
    const validateForm = (values) => {
        const warningMsg = {};
        
        if(!values.title){
            warningMsg.title = "Game Title is required";
        }
        if(!values.image){
            warningMsg.image = "Link to image is required";
        }
        if(!values.price){
            warningMsg.price = "Price is required";
        }
        if(!values.categories){
            warningMsg.categories = "Categories are required";
        }
        if(!values.platform){
            warningMsg.platform = "Platform is required";
        }
        console.log(warningMsg)
        return warningMsg;
        
    };


    return (
        <div className="product">
        <div className="header">
            <h1 className="productTitle">Update Game</h1>

            {
                    
            Object.values(warnings)
            .map((item) => 
            <Alert style={{ flex:"1",  margin: "10px 12px 0px 0px"}} variant="filled" severity="error">
                {item}
            </Alert>)
            }

            <Button variant="contained" color="primary" style={{color:"white"}} endIcon={<SendIcon />} onClick={handleUpdate()}>Update</Button>
        </div>
        <div className="productBody">
            <div className="productInfo">
                <div className="topInfo">
                    <h1 className="productTitle">{product.title}</h1>
                    <img src={product.image} alt="BattleField 2042" className="displayImage" />
                </div>
                <div className="bottomInfo">
                    <span className="infoTitle">Descripton</span>
                    <span className="info">{product.desc}</span>
                    <span className="infoTitle">Available on</span>
                    <span className="info">{product.platform}</span>
                    <span className="infoTitle">Price</span>
                    <span className="info">{product.price} &euro;</span>
                    <span className="infoTitle">Category</span>
                    <span className="info">{product.categories}</span>
                    <span></span>
                </div>
            </div>

            <div className="productUpdateInfo">
            <h3 className="productTitle">Update form</h3>
            <form action="" className="productForm">
        <div className="inputItem">
            <label >Game Title </label>
            <input name="title" type="text" className="productInput" />
        </div>
        <div className="inputItem">
            <label >Link to Image </label>
            <input name="image" type="text" className="productInput" />
        </div>
        <div className="inputItem">
            <label >Price</label>
            <input name="price" type="text" className="productInput" />
        </div>
        <div className="inputItem">
            <label >Description </label>
            <input name="desc" type="text" className="productInput" />
            </div>

        <div className="inputItem"> 
            <label >Platform </label>
            <input name="platform" type="text" className="productInput" />
        </div>
        <div className="inputItem">
            <label >Category</label>
            <input name="categories" type="text" className="productInput" />
        </div>
      
    </form>
            </div>

        </div>
    </div>

    )
}

export default AdminSingleProduct
