import React from 'react';
import styled from 'styled-components';
import { mobile } from '../responsive';
import { useForm } from './useForm';
import { useState, useEffect } from "react";


const Container = styled.div`

    width: 100vw;
    height: 100vh;
    background-image: linear-gradient(to bottom right, #3dc3b6,#1b2d51);
    display: flex;
    justify-content: center;
    align-items: center;
`

const Wrapper = styled.div`
    padding: 20px;
    width: 40%;
    background-color: white;
    ${mobile({
        width:"80%"
    })}

`

const Form = styled.form`
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
`

const Title = styled.h1`
    font-size: 24px;
    font-weight: 200;
`

const Input = styled.input`
    border-top-style: hidden;
    border-right-style: hidden;
    border-left-style: hidden;
    border-bottom-style: #1b2d51;
    background-color: white;
    outline: none;
    flex:1;
    min-width: 40%;
    margin: 25px 12px 0px 0px;
    ${mobile({
        
    })}
`

const Agreement = styled.span`
    margin-top: 20px;
    font-size: 12px;
    font-weight: 100;
    font-family: 'Roboto';
    ${mobile({
        fontSize:"10px"
    })}
`

const Button = styled.button`
    
    cursor: pointer;
    width: 100%;
    border: none;
    margin-top: 15px;
    color: white;
    padding: 5px;
    background-color: #1b2d51;
`

const Register = () => {

    const [values, setValues] = useState({
        username: '',
        email: '',
        password: '',
        passwordConf: ''
    });
    const [warnings, setWarnings] = useState({});
    const [submitFlag, setSubmitFlag] = useState(false);
    const handleChange = (event) => {
        
        const { name, value } = event.target;

        setValues({
            ...values,
            [name]: value
        });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        setWarnings(values)
        setSubmitFlag(true)
    }

    useEffect(() =>{
        if(Object.keys(warnings).length === 0 && submitFlag){
                console.log(values)
        }
    },[warnings]);

    const validateForm = (values) => {
        const warnings = {};

        if(!values.name){
            warnings.name = "Name is required";
        }
        if(!values.username){
            warnings.username = "Username is required";
        }
        if(!values.email){
            warnings.email = "Email is required";
        }
        if(!values.password){
            warnings.email = "Password is required";
        }
        if(!values.passwordConf){
            warnings.email = "Password is required";
        }
        
    };



    return (
        <Container>
            <Wrapper>
                <Title>Maak Account aan</Title>
                <Form onSubmit={handleSubmit}>
                    <Input placeholder="naam"
                    name="name"
                    value={values.name}
                    onChange={handleChange}
                    />
                    <Input placeholder="gebruikersnaam"
                    name="username"
                    value={values.username}
                    onChange={handleChange}
                    />
                    <Input placeholder="email"
                     name="email"
                     value={values.email}
                     onChange={handleChange}
                     />
                    <Input placeholder="wachtwoord"
                    name="password"
                    value={values.password}
                    onChange={handleChange}
                    />
                    <Input placeholder="wachtwoord bevestigen"
                     name="passwordConf"
                     value={values.passwordConf}
                     onChange={handleChange}
                    />
                    <Agreement>
                        Bij het aanmaken van een account, ... Lorem ipsum dolor sit amet consectetur adipisicing elit. Quaerat id facilis nam.
                    </Agreement>
                    <Button>Maak Account</Button>
                </Form>
            </Wrapper>
        </Container>
    )
}

export default Register
